var body = workflowContext.trigger.outputs.body;

var lines = body.split("\r\n");
var json = {
    positions: []
};
var positions = [];

lines.forEach(line => {
    if(line.startsWith("H::")) {
        json.orderDate = line.substring(3, 13).trim();
        json.orderId = line.substring(13, 29).trim();
        json.customerId = line.substring(29, 39).trim();
    } else if(line.startsWith("P::")) {
        json.positions.push({
            id: line.substring(3, 10).trim(),
            articleId: line.substring(10, 26).trim(),
            amount: line.substring(26, 34).trim(),
            price: line.substring(34, 46).trim(),
            total: line.substring(46, 56).trim()
        });
    }
});

return json;
